import React, { useState, useEffect, useContext } from 'react';
import { LanguageContext } from '../../LanguageProvider'; // Adjust the import path as needed
import "./collabse-list.css";

const Collabse = () => {
  const { language } = useContext(LanguageContext); // Use context to get current language
  const [faqs, setFaqs] = useState([]);
  const [activeIndex, setActiveIndex] = useState(null);

  useEffect(() => {
    // Fetching data from the API
    fetch('http://admin.monzarentcar.com/api/faq')
      .then(response => response.json())
      .then(data => {
        const formattedFaqs = data.map(item => ({
          question: item.question[language] || item.question.en, // Fallback to English if language not available
          answer: item.answer[language] || item.answer.en, // Fallback to English if language not available
        }));
        setFaqs(formattedFaqs);
      })
      .catch(error => console.error('Error fetching the FAQ data:', error));
  }, [language]); // Re-fetch data when language changes

  const toggleFAQ = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  return (
    <div className="faq-container">
      <h2>Frequently Asked Questions ___</h2>
      {faqs.map((faq, index) => (
        <div key={index} className="faq-item">
          <div className="faq-question" onClick={() => toggleFAQ(index)}>
            {faq.question}
            <span className={`faq-icon ${activeIndex === index ? 'open' : ''}`}>
              {activeIndex === index ? '-' : '+'}
            </span>
          </div>
          {activeIndex === index && (
            <div className="faq-answer">
              {faq.answer}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Collabse;
