import React, { useState, useEffect } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay, Thumbs } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/thumbs";
import "../../App.css";
import "../Related-Car/style.css";
import "./allcar.css"
import { useParams, Link } from "react-router-dom";
import { useTranslation } from "react-i18next";
const AllCar = () => {
  const { id } = useParams();
  const [slides, setSlides] = useState([]);
  const { t, i18n } = useTranslation();
  const [isLoading, setIsLoading] = useState(true); // حالة اللودينج
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://admin.monzarentcar.com/api/cars/search');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        console.log(data);

        // Parse and map the data as needed
        const fetchedSlides = data.cars.data.map((car) => ({
          id: car.id,
          src: car.images || ["/placeholder.jpg"], // Ensure it's an array
          name: car.name[i18n.language] || "No name available",
          description: car.description
            ? car.description[i18n.language]
            : "No description available",
          pricePerDay: car.price_per_day,
          pricePerMonth: car.price_per_month,
          doors: car.doors,
          passengers: car.passengers,
          bags: car.bags,
          brand: car.brand.image_url,
        }));
        setSlides(fetchedSlides);
        setIsLoading(false); // Stop loading once data is fetched
      } catch (error) {
        console.error("Error fetching data:", error);
        setIsLoading(false); // Stop loading even in case of an error
      }
    };
    fetchData();
  }, [i18n.language]);
  // Function to get the first 4 words of a title
  const getFirstFourWords = (title) => {
    const words = title.split(" ");
    return words.slice(0, 4).join(" ");
  };
  if (isLoading)
    return (
      <div className="loding-class-all">
        <img src="/loader.gif" alt="" width={200} />
      </div>
    );

  return (
    <div className="cars-section padd-top relatdcarsec">
      <nav>
        <div className="pt-bredwrap">
          <div className="container">
            <div className="ptboutr">
              <h2 className="mr-h1">{t("allCars")}</h2>
              <ol className="breadcrumb">
                <li>
                  <a aria-label="Home" href="/" className="fa fa-home" />
                </li>
                <li>
                  <a aria-label="" href="/">
                    {t("about.home")}
                  </a>
                </li>
                <li>
                  <a aria-label="" href="/">
                    {t("allCars")}
                  </a>
                </li>
              </ol>
            </div>
          </div>
        </div>
      </nav>
      <div className="container">
        <div className="fancytitle">
          {/* <h2>{t("relatedCars")}</h2> */}
          {/* <Link to="/all-car">{t("seeAll")}</Link> */}
        </div>
        <div
          className="carscolwrap"
          style={{ display: "flex", flexWrap: "wrap", padding: "20px" }}
        >
          {slides.map((slide, index) => (
            <div
              key={index}
              data-daily-price={slide.pricePerDay}
              className="carthumb"
            >
              <div className="ctimgcont">
                <div className="slider-container">
                  <Swiper
                    modules={[Navigation, Pagination, Autoplay, Thumbs]}
                    navigation
                    loop={true}
                    slidesPerView={1}
                    pagination={{ clickable: true }}
                    autoplay={{ delay: 3000 }}
                    className=""
                  >
                    {slide.src.map((src1, index) => (
                      <SwiperSlide key={index}>
                        <img
                          className="img"
                          style={{
                            width: "100%",
                            objectFit: "cover",
                            height: "auto",
                          }}
                          src={src1.image_url}
                          alt={slide.name}
                          title={getFirstFourWords(slide.name)}
                        />
                      </SwiperSlide>
                    ))}
                  </Swiper>
                  <img src={slide.brand} alt="" className="logoImg" />
                </div>
              </div>
              <div className="ctdtailcont">
                <div className="thumbtitle">
                  <Link to={`/cardetails/${slide.id}`}>
                    <h3>{getFirstFourWords(slide.name) || t("unknownCar")}</h3>

                  </Link>

                </div>
                <div className="pricewrap">
                  <div className="mainprice">
                    <div className="orignalprice">
                      AED <strong>{slide.pricePerDay}</strong>
                      <span> / {t("day")}</span>
                      <div className="distance">
                        <i className="fa-solid fa-road"></i> <span>250 km</span>
                      </div>
                    </div>
                    <div className="delprice">
                      AED {slide.pricePerDay + 150}
                    </div>
                  </div>
                </div>
                <div className="pricewrap mb-3">
                  <div className="mainprice">
                    <div className="orignalprice">
                      AED <strong>{slide.pricePerMonth}</strong>
                      <span> / {t("month")}</span>
                      <div className="distance">
                        <i className="fa-solid fa-road"></i>{" "}
                        <span>3500 km</span>
                      </div>
                    </div>
                    <div className="delprice">
                      AED {slide.pricePerMonth + 3000}
                    </div>
                  </div>
                </div>

                <div className="googlerating">
                  {[...Array(5)].map((_, index) => (
                    <svg key={index} className="svgicon star-icon active">
                      <use xlinkHref="#star-icon" />
                    </svg>
                  ))}
                </div>
                <ul className="thumbfeature">
                  <li>
                    <img loading="lazy" src="/1.svg" alt="door" title={t("door")} />
                    {slide.doors}
                  </li>
                  <li>
                    <img
                      loading="lazy"
                      src="/2.svg"
                      width={18}
                      height={18}
                      alt="passengers"
                      title={t("passengers")}
                    />
                    {slide.passengers}
                  </li>
                  <li>
                    <img
                      loading="lazy"
                      src="/3.svg"
                      width={18}
                      height={18}
                      alt="luggages"
                      title={t("luggages")}
                    />
                    {slide.bags}
                  </li>
                  <li>{t("suv")}</li>
                </ul>
                <ul className="thumboffer">
                  <li>
                    <svg className="svgicon circlecheck-icon">
                      <use xlinkHref="#circlecheck-icon" />
                    </svg>
                    {t("freeDelivery")}
                  </li>
                  <li>
                    <svg className="svgicon circlecheck-icon">
                      <use xlinkHref="#circlecheck-icon" />
                    </svg>
                    {t("insuranceIncluded")}
                  </li>
                </ul>
                <ul className="carthumb-cta">
                  <li>
                    <a
                      href={`https://api.whatsapp.com/send?phone=+971566681333&text=Hi, I'm contacting you through ${t("website")}. I'd like to inquire about ${slide.name}%20%0Ahttps://www.example.com/car-detail/${id}`}
                      target="_blank"
                      rel="noreferrer"
                      aria-label={t("whatsappInquiry")}
                    >
                      <svg className="svgicon whatsapp-icon">
                        <use xlinkHref="#whatsapp-icon" />
                      </svg>
                      {t("whatsapp")}
                    </a>
                  </li>
                  <li>
                    <a
                      href="tel:+971566681333"
                      target="_blank"
                      rel="noreferrer"
                      aria-label={t("callUsInquiry")}
                    >
                      <svg className="svgicon phone-icon">
                        <use xlinkHref="#phone-icon" />
                      </svg>
                      {t("callUs")}
                    </a>
                  </li>
                  <li>
                    <button
                      data-url={`https://www.example.com/car-detail/${id}`}
                      data-title={slide.name}
                      className="booknowpop"
                      aria-label={t("bookOnlineInquiry")}
                    >
                      <svg className="svgicon book-icon">
                        <use xlinkHref="#book-icon" />
                      </svg>
                      {t("bookOnline")}
                    </button>
                  </li>
                </ul>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AllCar;
