import React, { useContext, useEffect, useState } from 'react';
import { LanguageContext } from '../../LanguageProvider'; // Import LanguageContext
import './style.css';

const NowCar = () => {
  const { language, handleLanguageChange } = useContext(LanguageContext); // Use LanguageContext
  const [faqsData, setFaqsData] = useState([]);
  const [activeQuestionIndex, setActiveQuestionIndex] = useState(null);

  useEffect(() => {
    fetch(`http://admin.monzarentcar.com/api/faq`)
      .then(response => response.json())
      .then(data => {
        setFaqsData(data.slice(0, 3).map(faq => ({
          question: faq.question[language],
          answer: faq.answer[language],
        })));
      })
      .catch(error => console.error('Error fetching the FAQ data:', error));
  }, [language]);

  const toggleQuestion = (index) => {
    setActiveQuestionIndex(activeQuestionIndex === index ? null : index);
  };

  return (
    <section className="flex">
      <div className="new-car-class">
        <div className="faq-container oxpwrap">
          {faqsData.map((faq, index) => (
            <div key={index} className="faq-item">
              <div className="faq-question" onClick={() => toggleQuestion(index)}>
                {faq.question}
                <span className={`faq-icon ${activeQuestionIndex === index ? 'open' : ''}`}>
                  {activeQuestionIndex === index ? '-' : '+'}
                </span>
              </div>
              {activeQuestionIndex === index && (
                <div className="faq-answer">{faq.answer}</div>
              )}
            </div>
          ))}
        </div>
        <div className="car-image-new">
          <img src="/carimage.webp" alt="" width={300} />
        </div>
      </div>
    </section>
  );
};

export default NowCar;
