// import React from "react";
import { useState, useEffect, useContext } from "react";
import "./header.css";
import "../../App.css";
import "../../../src/ar-style.css";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { useTranslation } from "react-i18next";
import { LanguageContext } from "../../LanguageProvider";
import "@fortawesome/fontawesome-free/css/all.min.css";
// import * as React from 'react';
import React from "react";
import IconButton from "@mui/material/IconButton";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
const options = [
  "None",
  "Atria",
  "Callisto",
  "Dione",
  "Ganymede",
  "Hangouts Call",
  "Luna",
  "Oberon",
  "Phobos",
  "Pyxis",
  "Sedna",
  "Titania",
  "Triton",
  "Umbriel",
];

const ITEM_HEIGHT = 48;
const Header = () => {
  const { handleLanguageChange } = useContext(LanguageContext); // Use LanguageContext
  const [showModal, setshowModal] = useState(false);
  const [brands, setBrands] = useState([]);
  const [Categories, setCategories] = useState([]);
  const [Usd, setUsd] = useState([]);
  const [Cuntry, setCuntry] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBrands = async () => {
      try {
        const response = await fetch("http://admin.monzarentcar.com/api/brands");
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setBrands(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchBrands();
  }, []);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch("http://admin.monzarentcar.com/api/types");
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setCategories(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchCategories();
  }, []);

  useEffect(() => {
    const fetchUsd = async () => {
      try {
        const response = await fetch(
          "http://admin.monzarentcar.com/api/currencies"
        );
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setUsd(data);
        console.log(data[0].code);
      } catch (error) {
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    };
    fetchUsd();
  }, []);
  useEffect(() => {
    const fetchUCuntry = async () => {
      try {
        const response = await fetch(
          "http://admin.monzarentcar.com/api/countries"
        );
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setCuntry(data);
        console.log(data[0].code);
      } catch (error) {
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    };
    fetchUCuntry();
  }, []);
  // language toggle
  const [language, setLanguage] = useState("en"); // Default language is English
  const { t } = useTranslation();

  const toggleLanguage = (lang) => {
    if (lang === "en") {
      setLanguage("en");
    } else {
      setLanguage("ar");
    }
  };

  const { i18n } = useTranslation();
  const handleLanguageChangeClick = (lang) => {
    handleClose();
    handleLanguageChange(lang);
    i18n.changeLanguage(lang);
    toggleLanguage(lang); // Use context method for language change
  };
  // Update the direction globally whenever the language changes
  useEffect(() => {
    document.body.className = language === "en" ? "ltr" : "rtl";
  }, [language]);

  // meterial ui
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const [coinAnchorEl, setCoinAnchorEl] = useState(null);
  const [locationAnchorEl, setLocationAnchorEl] = useState(null);
  const [selectedCoin, setSelectedCoin] = useState("");
  const [selectedLocation, setSelectedLocation] = useState("");
  const [categoriesAnchorEl, setCategoriesAnchorEl] = useState(null);
  const [brandsAnchorEl, setBrandsAnchorEl] = useState(null); // New state for brands
  // List of coins and locations
  const coins = [
    { value: "USD", label: "USD" },
    { value: "EUR", label: "EUR" },
    { value: "AED", label: "AED" },
  ];

  const locations = [
    { value: "UAE", label: "UAE" },
    { value: "USA", label: "USA" },
    { value: "UK", label: "UK" },
  ];

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleCoinClick = (event) => {
    setCoinAnchorEl(event.currentTarget);
  };

  const handleLocationClick = (event) => {
    setLocationAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setCoinAnchorEl(null);
    setLocationAnchorEl(null);
    setAnchorEl(null);
    setCategoriesAnchorEl(null);
    setBrandsAnchorEl(null);
  };

  const handleCoinChange = (coin) => {
    setSelectedCoin(coin);
    handleClose();
  };

  const handleLocationChange = (location) => {
    setSelectedLocation(location);
    handleClose();
  };
  const handleCategoriesClick = (event) => {
    setCategoriesAnchorEl(event.currentTarget); // Open categories menu
