import React, { useState, useEffect } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay, Thumbs } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/thumbs";
import "../../App.css";
import "./style.css";
import { useParams, Link } from "react-router-dom";
import { useTranslation } from "react-i18next";

const FlashSales = () => {
  const { id } = useParams();
  const [countdown, setCountdown] = useState({
    hours: 0,
    minutes: 0,
    seconds: 0,
  });
  const [slides, setSlides] = useState([]);
  const [isLoading, setIsLoading] = useState(true); // حالة اللودينج
  const [error, setError] = useState(null); // حالة الخطأ
  const { i18n } = useTranslation();
  const { t } = useTranslation();
  // Fetch data from API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          "http://admin.monzarentcar.com/api/cars/offers"
        );
        const data = await response.json();
        const fetchedSlides = Object.values(data.cars)
          .slice(0, 4)
          .map((car) => ({
            id: car.id,
            src:
              car.images.length > 0
                ? car.images
                : [{ image_url: "/placeholder.jpg" }],
            name: car.name[i18n.language] || "Unknown Car",
            description: car.description?.en || "No description available",
            pricePerDay: car.price_per_day,
            pricePerMonth: car.price_per_month,
            doors: car.doors,
            passengers: car.passengers,
            bags: car.bags,
            brand: car.image_url,
            offer_amount: car.offer_amount,
            offer_duration: car.offer_duration,
            offer_start_time: car.offer_start_time, // وقت البدء
          }));
        setSlides(fetchedSlides);
        startCountdown(
          fetchedSlides[0].offer_start_time,
          fetchedSlides[0].offer_duration
        );
        setTimeout(() => {
          setIsLoading(false); // تأخير انتهاء التحميل لمدة 3 ثوانٍ إضافية
        }, 1000);
      } catch (error) {
        setError("Failed to fetch data");
        setIsLoading(false); // إيقاف اللودينج في حالة حدوث خطأ
      }
    };

    fetchData();
  }, [id, i18n.language]);

  // Function to start countdown
  const startCountdown = (startTime, durationInHours) => {
    const startTimeDate = new Date(startTime); // تحويل وقت البدء إلى كائن Date
    const targetTime =
      startTimeDate.getTime() + durationInHours * 60 * 60 * 1000; // حساب وقت الانتهاء بناءً على مدة العرض

    const interval = setInterval(() => {
      const currentTime = Date.now();
      const timeLeft = targetTime - currentTime;

      if (timeLeft <= 0) {
        clearInterval(interval);
        setCountdown({ hours: 0, minutes: 0, seconds: 0 });
      } else {
        const hours = Math.floor(timeLeft / (1000 * 60 * 60));
        const minutes = Math.floor((timeLeft % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((timeLeft % (1000 * 60)) / 1000);
        setCountdown({ hours, minutes, seconds });
      }
    }, 1000);
  };

  const getFirstFourWords = (title) => {
    const words = title.split(" ");
    return words.slice(0, 4).join(" ");
  };
  if (isLoading)
    return (
      <div className="loding-class">
        <img src="/loader.gif" alt="" width={200} />
      </div>
    );

  if (slides.length < 1) {
    return (
      <div className="offer-ended-container">
        <h1 className="offer-ended-title">لا يوجد عرض </h1>
        <p className="offer-ended-message">عذراً، لقد انتهت مدة العرض.</p>
      </div>
    );
  }
  return (
    <section>
      <div className="cars-section padd-top relatdcarsec">
        <div className="container">
          <div className="fancytitle">
            <h2>{t("offers.car-rental-offers")}</h2>
            <Link to="/all-car" className="link">
              See All
            </Link>
          </div>
          <div
            className="carscolwrap"
            style={{ display: "flex", flexWrap: "wrap", padding: "20px" }}
          >
            {slides.map((slide, index) => (
              <div key={index} data-daily-price={600} className="carthumb">
                <div className="ctimgcont">
                  <div className="slider-container">
                    <Swiper
                      modules={[Navigation, Pagination, Autoplay, Thumbs]}
                      navigation
                      loop={true}
                      slidesPerView={1}
                      pagination={{ clickable: true }}
                      autoplay={{ delay: 3000 }}
                    >
                      {slide.src.map((src1, index) => (
                        <SwiperSlide key={index}>
                          <img
                            className="img"
                            style={{
                              width: "100%",
                              objectFit: "cover",
                              height: "auto",
                            }}
                            src={src1.image_url}
                            alt={slide.name}
                            title={getFirstFourWords(slide.name)}
                          />
                        </SwiperSlide>
                      ))}
                    </Swiper>
                    <img src={slide.brand} alt="" className="logoImg" />
                  </div>
                </div>
                <div className="ctdtailcont">
                  <div className="thumbtitle">
                    <Link to={`/cardetails/${slide.id}`}>
                      <h3>{getFirstFourWords(slide.name) || "Unknown Car"}</h3>
                    </Link>
                  </div>

                  <div className="pricewrap">
                    <div className="mainprice p-3">
                      <div className="orignalprice">
                        AED{" "}
                        <strong>
                          {slide.pricePerDay - slide.offer_amount}
                        </strong>
                        <span> / Day</span>
                        <div className="distance">
                          <i className="fa-solid fa-road"></i>{" "}
                          <span>300 km</span>
                        </div>
                      </div>
                      <div className="delprice">
                        AED {slide.pricePerDay + 150}
                      </div>
                    </div>
                  </div>
                  <div className="pricewrap mb-3">
                    <div className="mainprice p-3">
                      <div className="orignalprice">
                        AED{" "}
                        <strong>
                          {slide.pricePerMonth - slide.offer_amount}
                        </strong>
                        <span> / Month</span>
                        <div className="distance">
                          <i className="fa-solid fa-road"></i>{" "}
                          <span>3500 km</span>
                        </div>
                      </div>
                      <div className="delprice">
                        AED {slide.pricePerMonth + 3000}
                      </div>
                    </div>
                  </div>

                  <ul className="thumboffer">
                    <li>
                      <svg className="svgicon circlecheck-icon">
                        <use xlinkHref="#circlecheck-icon" />
                      </svg>
                      Free Delivery
                    </li>
                    <li>
                      <svg className="svgicon circlecheck-icon">
                        <use xlinkHref="#circlecheck-icon" />
                      </svg>
                      Insurance Included
                    </li>
                  </ul>

                  <ul>
                    <div className="timer-container">
                      <div className="unit hours-unit">
                        <div className="time-row">
                          <div className="digit">
                            {String(countdown.hours).padStart(2, "0")[0]}
                          </div>
                          <div className="digit">
                            {String(countdown.hours).padStart(2, "0")[1]}
                          </div>
                        </div>
                        <div className="label">Hours</div>
                      </div>
                      <span className="time-separator">:</span>
                      <div className="unit minutes-unit">
                        <div className="time-row">
                          <div className="digit">
                            {String(countdown.minutes).padStart(2, "0")[0]}
                          </div>
                          <div className="digit">
                            {String(countdown.minutes).padStart(2, "0")[1]}
                          </div>
                        </div>
                        <div className="label my-1">Minutes</div>
                      </div>
                      <span className="time-separator">:</span>
                      <div className="unit seconds-unit">
                        <div className="time-row">
                          <div className="digit">
                            {String(countdown.seconds).padStart(2, "0")[0]}
                          </div>
                          <div className="digit">
                            {String(countdown.seconds).padStart(2, "0")[1]}
                          </div>
                        </div>
                        <div className="label">Seconds</div>
                      </div>
                    </div>
                  </ul>

                  <ul className="carthumb-cta">
                    <li>
                      <a
                        href={`https://api.whatsapp.com/send?phone=+971566681333&text=Hi, I'm contacting you through fastercars.ae. I'd like to inquire about ${slide.name}%20%0Ahttps://www.fastercars.ae/car-detail/${id}`}
                        target="_blank"
                        rel="noreferrer"
                        aria-label="WhatsApp Inquiry"
                      >
                        <svg className="svgicon whatsapp-icon">
                          <use xlinkHref="#whatsapp-icon" />
                        </svg>
                        WhatsApp
                      </a>
                    </li>
                    <li>
                      <a
                        href="tel:+971566681333"
                        target="_blank"
                        rel="noreferrer"
                        aria-label="Call Us Inquiry"
                      >
                        <svg className="svgicon phone-icon">
                          <use xlinkHref="#phone-icon" />
                        </svg>
                        Call Us
                      </a>
                    </li>

                    <li className="book-online">
                      <a
                        href="tel:+12345678910"
                        target="_blank"
                        rel="noreferrer"
                        aria-label="Book Online Inquiry"
                      >
                        <svg className="svgicon book-icon">
                          <use xlinkHref="#book-icon" />
                        </svg>
                        Book Online
                      </a>
                    </li>
                  </ul>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default FlashSales;
