import React, { useState, useEffect } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay, Thumbs } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/thumbs';
import '../../App.css';
import "./style.css";
import { useParams, Link } from 'react-router-dom';
import { useTranslation } from "react-i18next";
const RelatedCar = () => {
  const { id } = useParams();
  const [slides, setSlides] = useState([]);
  const { i18n } = useTranslation();
  const { t } = useTranslation();
  const [isLoading, setIsLoading] = useState(true); // حالة اللودينج


  // Fetch data from API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://admin.monzarentcar.com/api/cars/search');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        console.log(data);

        // Parse and map the data as needed
        const fetchedSlides = data.cars.data.slice(0, 4).map(car => ({
          id: car.id,
          src: car.images || ['/placeholder.jpg'], // Use an array with a default image if not available
          name: car.name[i18n.language],
          description: car.description ? car.description.en : 'No description available',
          pricePerDay: car.price_per_day,
          pricePerMonth: car.price_per_month,
          doors: car.doors,
          passengers: car.passengers,
          bags: car.bags,
          brand: car.brand.image_url,
        }));
        setSlides(fetchedSlides);
        setTimeout(() => {
          setIsLoading(false); // تأخير انتهاء التحميل لمدة 3 ثوانٍ إضافية
        }, 1000);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [id, i18n.language]);

  // Function to get the first 4 words of a title
  const getFirstFourWords = (title) => {
    const words = title.split(' ');
    return words.slice(0, 4).join(' ');
  };

  if (isLoading)
    return (
      <div className="loding-class">
        <img src="/loader.gif" alt="" width={200} />
      </div>
    );


  return (
    <div className="cars-section padd-top relatdcarsec">
      <div className="container">
        <div className="fancytitle">
          <h2>{t("offers.related-cars")}</h2>
          <Link to="/all-car">See All</Link>
        </div>
        <div className="carscolwrap" style={{ display: "flex", flexWrap: "wrap", padding: "20px" }}>
          {slides.map((slide, index) => (
            <div key={index} data-daily-price={slide.pricePerDay} className="carthumb">
              <div className="ctimgcont">
                <div className="slider-container">
                  <Swiper
                    modules={[Navigation, Pagination, Autoplay, Thumbs]}
                    navigation
                    loop={true}
                    slidesPerView={1}
                    pagination={{ clickable: true }}
                    autoplay={{ delay: 3000 }}
                    className=""
                  >
          {slide.src.map((src1, index) => (
                    <SwiperSlide>

                      <img
                        className="img"
                        style={{ width: '100%', objectFit: 'cover', height: "auto" }}

                        src={src1.image_url}


                        alt={slide.name}
                        title={getFirstFourWords(slide.name)}
                      />
                    </SwiperSlide>
          ))}
                  </Swiper>
                  <img src={slide.brand} alt="" className='logoImg' />
                </div>
              </div>
              <div className="ctdtailcont">
                <div className="thumbtitle">
                  <Link to={`/cardetails/${slide.id}`}>
                    <h3>{getFirstFourWords(slide.name) || 'Unknown Car'}</h3>
                  </Link>
                </div>
                <div className="pricewrap">
                  <div className="mainprice">
                    <div className="orignalprice">
                      AED <strong>{slide.pricePerDay}</strong><span> / Day</span>
                      <div className='distance'><i className="fa-solid fa-road"></i> <span>250 km</span></div>
                    </div>
                    <div className="delprice">AED {slide.pricePerDay + 150}</div>
                  </div>
                </div>
                <div className="pricewrap mb-3">
                  <div className="mainprice">
                    <div className="orignalprice">
                      AED <strong>{slide.pricePerMonth}</strong><span> / Month</span>
                      <div className='distance'><i className="fa-solid fa-road"></i> <span>3500 km</span></div>
                    </div>
                    <div className="delprice">AED {slide.pricePerMonth + 3000}</div>
                  </div>
                </div>
                <div className="googlerating">
                  {[...Array(5)].map((_, index) => (
                    <svg key={index} className="svgicon star-icon active">
                      <use xlinkHref="#star-icon" />
                    </svg>
                  ))}
                </div>
                <ul className="thumbfeature">
                  <li>
                    <img
                      loading="lazy"
                      src="/1.svg"
                      alt="door"
                      title="door"
                    />
                    {slide.doors}
                  </li>
                  <li>
                    <img
                      loading="lazy"
                      src="/2.svg"
                      width={18}
                      height={18}
                      alt="passengers"
                      title="passengers"
                    />
                    {slide.passengers}
                  </li>
                  <li>
                    <img
                      loading="lazy"
                      src="/3.svg"
                      width={18}
                      height={18}
                      alt="luggages"
                      title="luggages"
                    />
                    {slide.bags}
                  </li>
                  <li>SUV</li>
                </ul>
                <ul className="thumboffer">
