import React, { useCallback, useRef, useState } from 'react';
import "../../App.css";
import "./video.css";

const Video = () => {
    const [playingVideoId, setPlayingVideoId] = useState(null);
    const videoRefs = useRef({});

    const handlePlayPause = useCallback((id) => {
        setPlayingVideoId((prevId) => {
            if (prevId === id) {
                videoRefs.current[id]?.pause();
                return null;
            }
            videoRefs.current[prevId]?.pause();
            videoRefs.current[id]?.play();
            return id;
        });
    }, []);

    return (
        <div className='video-gallery'>
            {[
                { id: 1, title: 'Car Headlight Blinking' },
                { id: 3, title: 'Sports Car Overview' },
                { id: 4, title: 'SUV Adventure' },
                { id: 5, title: 'Night Drive' },
            ].map(({ id, title }) => (
                <div key={id} className='video-container'>
                    <video
                        ref={(ref) => (videoRefs.current[id] = ref)}
                        src={`/stock-footage-car-headlight-blinking-in-dark-sports-car-headlight.webm?${id}`}
                        className='video-element'
                        preload='auto'
                    ></video>

                    <div className='video-title'>
                        <h4>{title}</h4>
                        <div className='play-button' onClick={() => handlePlayPause(id)}>
                            <span>{playingVideoId === id ? '❚❚' : '▶'}</span>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Video;
