import React, { useState, useEffect } from "react";
import "./listcar.css";

const Listcar = () => {
  const [carTypes, setCarTypes] = useState([]);
  const [showMore, setShowMore] = useState(false);

  useEffect(() => {
    fetch("http://admin.monzarentcar.com/api/types")
      .then((response) => response.json())
      .then((data) => {
        setCarTypes(data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const handleViewMore = () => {
    setShowMore(!showMore);
  };
  const images = [
    "/lis1.webp",
    "/lis2.webp",
    "/lis3.webp",
    "/lis4.webp",
    "/lis5.webp",
    "/sports2.webp"
  ];
  const data = [
    { id: 1, title: { en: 'SVC' }, image_url: '/4.webp', cars: 1 },
    { id: 2, title: { en: 'MAR' }, image_url: '/5.webp', cars: 13 },
    { id: 3, title: { en: 'ROLS' }, image_url: '/6.webp', cars: 15 },
    { id: 4, title: { en: 'MG' }, image_url: '/2.webp', cars: 5 },
    { id: 5, title: { en: 'MINI' }, image_url: '/1.webp', cars: 10 },
    { id: 5, title: { en: 'MINI' }, image_url: '/4.webp', cars: 10 },
    { id: 5, title: { en: 'MINI' }, image_url: '/6.webp', cars: 10 },
    { id: 5, title: { en: 'MINI' }, image_url: '/5.webp', cars: 10 },
    { id: 5, title: { en: 'MINI' }, image_url: '/1.webp', cars: 10 },
  ];
  return (
    <div>
      <div className="car-list flex">

      <div className="car-list flex mini-cars">
          {data.slice(0, showMore ? carTypes.length : 5).map((car,key) => (
            <li key={key}>
              <a href="#!">
                <div className="img-back">
                  <img src={car.image_url} alt="" />
                </div>
                <h3>{car.title.en}</h3>
                <span>{car.cars} </span>
              </a>
            </li>
          ))}
        </div>
        {/* <ul>
          {carTypes.slice(0, showMore ? carTypes.length : 6).map((car, index) => {
            // Select a random image from the images array
            const randomImage = images[Math.floor(Math.random() * images.length)];

            return (
              <li key={index}>
                <a href="#!">
                  <div className="img-back">
                    <img src={randomImage} alt="" />
                  </div>
                  <h3>{car.title.en}</h3>
                  <p>{car.cont} </p>
                  <p className="car-count">10 cars</p>
                </a>
              </li>
            );
          })}
        </ul> */}
      </div>
      <div className="more-car">
        <button onClick={handleViewMore}>
          {showMore ? "View Less" : "View More"}
          <svg className="svgicon angle-icon">
            <use xlinkHref="#angle-icon" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default Listcar;
