import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import image2 from "../../sliderImgs/1.jpg";
import image1 from "../../sliderImgs/5.jpg";
import image3 from "../../sliderImgs/6.jpg";
import image4 from "../../sliderImgs/7.jpg";
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

const ImgSlider = () => {
  const { t } = useTranslation();

  let settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
  };

  return (
    <div className='main-slider'>
      <Slider className="carousel" {...settings}>
        <div className="wrap">
          <a href="#">
            <img src={image1} alt="Slider Image 1" loading='lazy' />
          </a>
        </div>
        <div className="wrap">
          <a href="#">
            <img src={image2} alt="Slider Image 2" />
          </a>
        </div>
        <div className="wrap">
          <a href="#">
            <img src={image3} alt="Slider Image 3" />
          </a>
        </div>
        <div className="wrap">
          <a href="#">
            <img src={image4} alt="Slider Image 4" />
          </a>
        </div>
      </Slider>
      <div className='main-class'>
        <h2>{t('slider.heading')}</h2>
        <h4>{t('slider.subheading')}</h4>
        <Link to="/all-car">
          <button>{t('slider.buttonText')}</button>
        </Link>
      </div>
    </div>
  );
};

export default ImgSlider;
