import React, { useState, useEffect } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay, Thumbs } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/thumbs";
import "../../App.css";
import "../Related-Car/style.css";
import "./allcar.css"
import { useParams, Link } from "react-router-dom";
import { useTranslation } from "react-i18next";
const AllCar = () => {
  const { id } = useParams();
  const [slides, setSlides] = useState([]);
  const { t, i18n } = useTranslation();
  const [isLoading, setIsLoading] = useState(true); // حالة اللودينج
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://admin.monzarentcar.com/api/cars/search');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        console.log(data);

        // Parse and map the data as needed
        const fetchedSlides = data.cars.data.map((car) => ({
          id: car.id,
          src: car.images || ["/placeholder.jpg"], // Ensure it's an array
          name: car.name[i18n.language] || "No name available",
          description: car.description
            ? car.description[i18n.language]
            : "No description available",
          pricePerDay: car.price_per_day,
          pricePerMonth: car.price_per_month,
          doors: car.doors,
          passengers: car.passengers,
          bags: car.bags,
          brand: car.brand.image_url,
        }));
        setSlides(fetchedSlides);
        setIsLoading(false); // Stop loading once data is fetched
      } catch (error) {
        console.error("Error fetching data:", error);
        setIsLoading(false); // Stop loading even in case of an error
      }
    };
    fetchData();
  }, [i18n.language]);
  // Function to get the first 4 words of a title
  const getFirstFourWords = (title) => {
    const words = title.split(" ");
    return words.slice(0, 4).join(" ");
  };
  if (isLoading)
    return (
      <div className="loding-class-all">
        <img src="/loader.gif" alt="" width={200} />
      </div>
    );

  return (
    <div className="cars-section padd-top relatdcarsec">
      <nav>
        <div className="pt-bredwrap">
          <div className="container">
            <div className="ptboutr">
              <h2 className="mr-h1">{t("allCars")}</h2>
              <ol className="breadcrumb">
                <li>
                  <a aria-label="Home" href="/" className="fa fa-home" />
                </li>
                <li>
                  <a aria-label="" href="/">
                    {t("about.home")}
                  </a>
                </li>
                <li>
                  <a aria-label="" href="/">
                    {t("allCars")}
                  </a>
                </li>
              </ol>
            </div>
          </div>
        </div>
      </nav>
      <div className="container">
        <div className="fancytitle">
          {/* <h2>{t("relatedCars")}</h2> */}
          {/* <Link to="/all-car">{t("seeAll")}</Link> */}
        </div>
        <div
          className="carscolwrap"
          style={{ display: "flex", flexWrap: "wrap", padding: "20px" }}
        >
          {slides.map((slide, index) => (
            <div
              key={index}
              data-daily-price={slide.pricePerDay}
              className="carthumb"
            >
