import React, { useState, useEffect } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay } from "swiper/modules";
// Import Swiper styles
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/scrollbar";
import "./BrabdsSlider.css";

// رابط الصورة الافتراضية
const DEFAULT_IMAGE = "/b21.webp"; // يمكنك استخدام رابط الصورة الافتراضية الذي تفضله

const BrandsSlider = () => {
  const [brands, setBrands] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBrands = async () => {
      try {
        const response = await fetch('http://admin.monzarentcar.com/api/brands');
        if (!response.ok) {
          throw new Error('Failed to fetch brands');
        }
        const data = await response.json();
        setBrands(data);

        // إضافة تأخير بعد جلب البيانات
        setTimeout(() => {
          setIsLoading(false); // تأخير انتهاء التحميل لمدة 3 ثوانٍ إضافية
        }, 1000); // يمكنك تغيير قيمة 3000 حسب المدة التي ترغب بها (بالمللي ثانية)

      } catch (error) {
        setError(error.message);
        setIsLoading(false); // حتى لو حدث خطأ، نوقف التحميل
      }
    };

    fetchBrands();
  }, []);

  if (isLoading) return <div className="loding-class"> 
  <img src="/loader.gif" alt=""  width={200}/>
  </div>;
  if (error) return <p>Error: {error}</p>;
  return (
    <div className="bg-class-sliders">
      <Swiper
        modules={[Navigation, Pagination, Autoplay]}
        spaceBetween={10}
        slidesPerView={10}
        navigation
        scrollbar={{ draggable: true }}
        loop={true}
        autoplay={{ delay: 3000, disableOnInteraction: false }}
        onSwiper={(swiper) => console.log(swiper)}
        onSlideChange={() => console.log("slide change")}
      >
        {brands.map((brand) => (
          <SwiperSlide key={brand.id}>
            <div className="brand-1">
              <a href="#!">
                <img
                  src={brand.image_url || DEFAULT_IMAGE} // استخدام الصورة الافتراضية إذا لم توجد صورة
                />
                <p>{brand.title.en}</p>
                <p>{brand.cars} Cars</p>
              </a>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default BrandsSlider;
